/////////////////////////////////////////////////////////////////////////////
// CMainFrame message handlers
void CMainFrame::OnNcMouseMove( UINT nHitTest,
CPoint point)
{
	CClientDC clientDC(this);
	char s[20];
	wsprintf(s, "X=%d  Y=%d   ", point.x, point.y);
	clientDC.TextOut(20, 20, s);
	CFrameWnd::OnNcMouseMove(nHitTest,point);
}

